import React, { useState } from "react";

export const TodoListBody = () => {
  const [todos, setTodos] = useState([]);
  const [newTodo, setNewTodo] = useState("");

  const handleAddTodo = (e) => {
    if (newTodo.trim() !== "") {
      setTodos([...todos, { text: newTodo.trim(), checked: false }]);
      setNewTodo("");
    }
  };

  const handleDeleteTodo = (id) => {
    const newTodos = [...todos];
    newTodos.splice(id, 1);
    setTodos(newTodos);
  };

  const handleToggleTodo = (id) => {
    const newTodos = [...todos];
    newTodos[id].checked = !newTodos[id].checked;
    setTodos(newTodos);
  };

  return (
    <div>
      <h1>TodoList body</h1>
      <input type="text" value={newTodo} onChange={(e) => setNewTodo(e.target.value)} />
      <label
        className="relative block overflow-hidden rounded-md border border-gray-200 px-3 pt-3 shadow-sm focus-within:border-blue-600 focus-within:ring-1 focus-within:ring-blue-600 dark:border-gray-700 dark:bg-gray-800"
      >
        <input
          type="text"
          placeholder="Nouvelle tâche"
          className="peer h-8 w-full border-none bg-transparent p-0 placeholder-transparent focus:border-transparent focus:outline-none focus:ring-0 dark:text-white sm:text-sm"
        />

        <span
          className="absolute start-3 top-3 -translate-y-1/2 text-xs text-gray-700 transition-all peer-placeholder-shown:top-1/2 peer-placeholder-shown:text-sm peer-focus:top-3 peer-focus:text-xs dark:text-gray-200"
        >
          Nouvelle tâche
        </span>
      </label>
      <button onClick={handleAddTodo}>Ajouter</button>
      <ul>
        {todos.map((todo, id) => (
          <li key={id}>
            <input type="checkbox" checked={todo.checked} onChange={() => handleToggleTodo(id)} />
            <span>{todo.text}</span>
            <button onClick={() => handleDeleteTodo(todo.id)}> Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  )
};
